import React, { Component } from 'react';

const buttonStyle = {
  margin: "10px"
};

const formStyle = {
  border: "1px solid black",
  display: "block",
  padding: "20px",
  width: "40%"
};

const inputStyle = {
  marginBottom: "10px"
};


const Button = props => {
  return (
    <button
      style={props.style}
      onClick={props.action}
    >
      {props.title}
    </button>
  );
};

const Input = props => {
  return (
    <div className="form-group">
      <label>
        {props.title}
      </label>
      <input
        className="form-control"
        id={props.name}
        name={props.name}
        type={props.inputtype}
        value={props.value}
        onChange={props.handleChange}
        placeholder={props.placeholder}
        {...props}
      />
    </div>
  );
};

class FormContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      formInfo: {
        firstName: "",
        lastName:""
      },

    };
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
    this.handleInput = this.handleInput.bind(this);
  }


  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    console.log(name + " " + value);
    this.setState(
      prevState => ({
        formInfo: {
          ...prevState.formInfo,
          [name]: value
        }
      }),
    );
  }



  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.formInfo;

    fetch("http://example.com", {
      method: "POST",
      body: JSON.stringify(userData),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(response => {
      response.json().then(data => {
        console.log("Successful" + data);
      });
    });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      formInfo: {
        firstName: "",
        lastName: "",
        about: ""
      }
    });
  }

  render() {
    return (
      <div>
        <form className="mainForm" onSubmit={this.handleFormSubmit} style={formStyle}>
          <Input
            inputtype={"text"}
            title={"First Name: "}
            name={"firstName"}
            value={this.state.formInfo.firstName}
            placeholder={"Enter your first name"}
            handleChange={this.handleInput}
            style={inputStyle}
          />

          <Input
            inputtype={"text"}
            title={"Last Name: "}
            name={"lastName"}
            value={this.state.formInfo.lastName}
            placeholder={"Enter your last name"}
            handleChange={this.handleInput}
            style={inputStyle}
          />

          <Button
            action={this.handleFormSubmit}
            type={"submit"}
            title={"Submit"}
            style={buttonStyle}
          />

          <Button
            action={this.handleClearForm}
            type={"clear"}
            title={"Clear"}
            style={buttonStyle}
          />
        </form>
        <div>
          {this.state.formInfo.firstName}
        </div>
        <div>
          {this.state.formInfo.lastName}
        </div>
        <pre>
        <code>

        </code>
        </pre>
      </div>
    );
  }
}

export default FormContainer;
