  }],
  "targeting_key" : "amznslots",
  "video_placement_targeting_enabled": false,
  "competitiveseparation_enabled": true,
"aax_category_to_labels_map": {
"2000":[],
"4001":[],
"4002":["AUTO & TRUCK RENTAL SVCS","Automobile Dealerships", "AUTOMOTIVE"],
"4003":["COSMETICS & BEAUTY AIDS","FRAGRANCES", "hair care", "hygiene", "johnson johnson eyecare", "johnson johnson skin care", "MOVIE THEATRE", "music", "perfume", "shoes", "health and beauty products"],
"4005":["Apple - Non-Compete", "ELECTRONIC EQUIP", "electronics", "GAME-VIDEO SYS"],
"4006":["EDUCATIONAL PROGRAM","NOTEPAPER"],
"4007":["games","movie promotions"],
"4010":["baby food","BABY PDTS"],
"4011":["FINANCIAL","insurance", "TAX SVCS", "taxes", "BANKS"],
"4012":["BANKS"],
"4013":["beverages","confectionary advertisers", "fast food", "Food - Quick Service (QSR)", "Food - Table Service", "FOODS & FOOD PRODUCTS", "RESTAURANT"],
"4015":["military"],
"4019":["DRUG REMEDIES-OTC","EXERCISE EQUIPMENT", "eyecare", "FEMININE HYGIENE PRODUCTS", "health categories", "HOSPITAL", "weight control", "health and beauty products"],
"4022":["cleaning supplies","glad food storage", "household supplies", "retail - hardware", "trash bags"],
"4025":["books","BROADCAST TV NETWORK", "PSA", "television"],
"4026":["HOUSEHOLD PDTS","pet supplies"],
"4029":["cloathing","consumer staples", "retail advertisers", "RETAIL STORES"],
"4031":["internet service","INTERNET SVCS", "wireless advertisers"],
"4033":["airline","CRUISES"],
},
  "order_prefix": "Full Eps Video_G",
  "lineitem_prefix": "Full Eps Video_G",
  "task_name": "dfp-create-setup-ott",
  "client_secret": "Z2gPApCulSAX3Cj9DbO7HVSs",
  "client_id": "136982470967-35btkf8u5ooak91keaqatr2ep348v339.apps.googleusercontent.com",
  "refresh_token": "1/jbw-ReBCdaeeaVQIIkmelDf251pPzVa_Z53rokPT_S8",
  "network_id": "4266",
  "encoding_key": "",
  "callback_url": "adasd",
  "sizes": ["480x360v"],
  "placement_ids": "1118258, 1115498, 1111058",
  "ad_unit_names": "",
  "sourceId": "0",
  "trafficker_id": "245114637",
  "advertiser_id": "4635577434",
  "creative_name": "",
  "encoded_policy_value": "c75udc",
  "start_time": "2018-12-15T00:00:00",
  "end_time": "2018-12-31T00:00:00",
  "impression_target": "5000"
}

let textPayload = JSON.stringify

const buttonStyle = {
  margin: "10px"
};

const formStyle = {
  border: "1px solid black",
  display: "block",
  padding: "20px",
  width: "40%"
};

const inputStyle = {
  marginBottom: "10px"
};


const Button = props => {
  return (
    <button
      style={props.style}
      onClick={props.action}
    >
      {props.title}
    </button>
  );
};

const Input = props => {
  return (
    <div className="form-group">
      <label>
        {props.title}
      </label>
      <input
        className="form-control"
        id={props.name}
        name={props.name}
        type={props.inputtype}
        value={props.value}
        onChange={props.handleChange}
        placeholder={props.placeholder}
        {...props}
      />
    </div>
  );
};

class FormContainer extends Component {
  constructor(props) {
    super(props);

    this.state = {
      formInfo: {
        firstName: "",
        lastName:""
      },

    };
    this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.handleClearForm = this.handleClearForm.bind(this);
    this.handleInput = this.handleInput.bind(this);
  }


  handleInput(e) {
    let value = e.target.value;
    let name = e.target.name;
    console.log(name + " " + value);
    this.setState(
      prevState => ({
        formInfo: {
          ...prevState.formInfo,
          [name]: value
        }
      }),
    );
  }



  handleFormSubmit(e) {
    e.preventDefault();
    let userData = this.state.formInfo;

    fetch("http://example.com", {
      method: "POST",
      body: JSON.stringify(userData),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      }
    }).then(response => {
      response.json().then(data => {
        console.log("Successful" + data);
      });
    });
  }

  handleClearForm(e) {
    e.preventDefault();
    this.setState({
      formInfo: {
        firstName: "",
        lastName: "",
        about: ""
      }
    });
  }

  render() {
    return (
      <div>
        <form className="mainForm" onSubmit={this.handleFormSubmit} style={formStyle}>
          <Input
            inputtype={"text"}
            title={"First Name: "}
            name={"firstName"}
            value={this.state.formInfo.firstName}
            placeholder={"Enter your first name"}
            handleChange={this.handleInput}
            style={inputStyle}
          />

          <Input
            inputtype={"text"}
            title={"Last Name: "}
            name={"lastName"}
            value={this.state.formInfo.lastName}
            placeholder={"Enter your last name"}
            handleChange={this.handleInput}
            style={inputStyle}
          />

          <Button
            action={this.handleFormSubmit}
            type={"submit"}
            title={"Submit"}
            style={buttonStyle}
          />

          <Button
            action={this.handleClearForm}
            type={"clear"}
            title={"Clear"}
            style={buttonStyle}
          />
        </form>
        <div>
          {this.state.formInfo.firstName}
        </div>
        <div>
          {this.state.formInfo.lastName}
        </div>
        <pre>
        <code>

        </code>
        </pre>
      </div>
    );
  }
}

export default FormContainer;
